import numpy as np

class BlackDermanToyModel:
    def __init__(self, r0, sigma, T, n):
        self.r0 = r0
        self.sigma = sigma
        self.T = T
        self.n = n

    def simulate(self):
        dt = self.T / self.n
        r = np.zeros(self.n)
        r[0] = self.r0
        for t in range(1, self.n):
            r[t] = r[t-1] + self.sigma * np.sqrt(dt) * np.random.normal()
        return r
