import numpy as np

class VasicekModel:
    def __init__(self, r0, kappa, theta, sigma, T, n):
        self.r0 = r0
        self.kappa = kappa
        self.theta = theta
        self.sigma = sigma
        self.T = T
        self.n = n

    def simulate(self):
        dt = self.T / self.n
        r = np.zeros(self.n)
        r[0] = self.r0
        for t in range(1, self.n):
            dr = self.kappa * (self.theta - r[t-1]) * dt + self.sigma * np.sqrt(dt) * np.random.normal()
            r[t] = r[t-1] + dr
        return r
