import unittest
from stochastic_models.heston_model import HestonModel

class TestHestonModel(unittest.TestCase):
    def test_simulation(self):
        model = HestonModel(v0=0.04, kappa=2.0, theta=0.04, sigma=0.3, rho=-0.7, s0=100, T=1, n=1000)
        S, V = model.simulate()
        self.assertEqual(len(S), 1000)
        self.assertEqual(len(V), 1000)

if __name__ == '__main__':
    unittest.main()
